import { Component, Inject } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { Alumno } from '../model/alumno';

@Component({
  selector: 'app-alumno-dialog',
  templateUrl: './alumno-dialog.component.html',
  styleUrls: ['./alumno-dialog.component.scss']
})
export class AlumnoDialogComponent {
  alumno: Alumno;

  constructor(
    public dialogRef: MatDialogRef<AlumnoDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {
    this.alumno = data.alumno || {};
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  onSaveClick(): void {
    this.dialogRef.close(this.alumno);
  }
}
<h2 mat-dialog-title>{{data.action}} Alumno</h2>
<div mat-dialog-content>
  <mat-form-field>
    <input matInput placeholder="Primer Nombre" [(ngModel)]="alumno.primerNombre" required>
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Segundo Nombre" [(ngModel)]="alumno.segundoNombre">
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Primer Apellido" [(ngModel)]="alumno.primerApellido" required>
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Segundo Apellido" [(ngModel)]="alumno.segundoApellido">
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Identificación" [(ngModel)]="alumno.identificacion" required>
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Correo Electrónico" [(ngModel)]="alumno.correoElectronico">
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Código Estudiantil" [(ngModel)]="alumno.codigoEstudiantil" required>
  </mat-form-field>
  <mat-form-field>
    <input matInput placeholder="Teléfono" [(ngModel)]="alumno.telefono">
  </mat-form-field>
</div>
<div mat-dialog-actions>
  <button mat-button (click)="onNoClick()">Cancelar</button>
  <button mat-button color="primary" (click)="onSaveClick()">Guardar</button>
</div>
