import { Component, OnInit } from '@angular/core';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { AlumnoDialogComponent } from '../alumno-dialog/alumno-dialog.component';
import { Alumno } from '../model/alumno';

@Component({
  selector: 'app-alumnos',
  templateUrl: './alumnos.component.html',
  styleUrls: ['./alumnos.component.scss']
})
export class AlumnosComponent implements OnInit{
  alumnos: Alumno[] = [
    {
      primerNombre: 'Juan',
      primerApellido: 'Pérez',
      identificacion: '12345',
      correoElectronico: 'juan@example.com',
      codigoEstudiantil: 'ST123',
      telefono: '123-456-7890'
    },
    {
      primerNombre: 'María',
      primerApellido: 'Gómez',
      identificacion: '67890',
      correoElectronico: 'maria@example.com',
      codigoEstudiantil: 'ST456',
      telefono: '987-654-3210'
    },
  ]// ...otros alumnos

  displayedColumns: string[] = ['nombre', 'correo', 'acciones'];

  constructor(public dialog: MatDialog) {}

  ngOnInit() {}

  openDialog(action: string, alumno?: Alumno): void {
    const dialogRef = this.dialog.open(AlumnoDialogComponent, {
      width: '250px',
      data: { action, alumno }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        if (action === 'Agregar') {
          this.alumnos.push(result);
        } else if (action === 'Editar') {
          const index = this.alumnos.findIndex(a => a.codigoEstudiantil === result.codigoEstudiantil);
          this.alumnos[index] = result;
        } else if (action === 'Eliminar') {
          const index = this.alumnos.findIndex(a => a.codigoEstudiantil === result.codigoEstudiantil);
          this.alumnos.splice(index, 1);
        }
      }
    });
  }

}
<button mat-button (click)="openDialog('Agregar')">Agregar Alumno</button>

<table mat-table [dataSource]="alumnos">
  <!-- Columnas de la tabla -->
  <ng-container matColumnDef="nombre">
    <th mat-header-cell *matHeaderCellDef> Nombre </th>
    <td mat-cell *matCellDef="let alumno"> {{alumno | nombreCompleto}} </td>
  </ng-container>
  <ng-container matColumnDef="correo">
  <th mat-header-cell *matHeaderCellDef> Correo </th>
  <td mat-cell *matCellDef="let alumno"> {{alumno.correoElectronico}} </td>
</ng-container>

  <!-- Agrega las demás columnas de la misma manera -->

  <!-- Agregar columnas para las acciones -->
  <ng-container matColumnDef="acciones">
    <th mat-header-cell *matHeaderCellDef> Acciones </th>
    <td mat-cell *matCellDef="let alumno">
      <button mat-icon-button (click)="openDialog('Editar', alumno)">
        <mat-icon>edit</mat-icon>
      </button>
      <button mat-icon-button (click)="openDialog('Eliminar', alumno)">
        <mat-icon>delete</mat-icon>
      </button>
    </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
