import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators, AbstractControl } from '@angular/forms';

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.scss']
})
export class FormularioComponent {
  formulario: FormGroup;

  constructor(private fb: FormBuilder) {
    this.formulario = this.fb.group({
      nombre: ['', [Validators.required, this.customValidator]],
      email: ['', [Validators.required, Validators.email]],
    });
  }

  customValidator(control: AbstractControl) {
    const value = control.value;
    if (value && value.toLowerCase() === 'ninguno') {
      return { customError: 'No se permite "Ninguno" como nombre.' };
    }
    if(value && value.length <= 3){
      return { customError: 'El nombre es muy corto.' };
    }
    return null;
  }
}
<form [formGroup]="formulario">
    <div>
      <label for="nombre">Nombre</label>
      <input type="text" id="nombre" formControlName="nombre">
      <div class="error-text">
        <p *ngIf="formulario.get('nombre')?.hasError('required')">El nombre es requerido.</p>
        <p *ngIf="formulario.get('nombre')?.hasError('customError')">{{ formulario.get('nombre')?.getError('customError') }}</p>
      </div>
    </div>
    <div class="margen-inferior">
      <label for="email">Email</label>
      <input type="text" id="email" formControlName="email">
      <div class="error-text">
        <p *ngIf="formulario.get('email')?.hasError('required')">El correo electronico es requerido.</p>
        <p *ngIf="formulario.get('email')?.hasError('email')">El correo electronico no es valido.</p>
      </div>
    </div>
    <button type="submit" [disabled]="formulario.invalid">Enviar</button>
</form>
